mapper.select:
		SELECT aa.*
		FROM
			(SELECT rownum rn, a.*
			FROM
				(SELECT map.obj_id, obj.obj_nm, map.mp_dwg_serno, map.mp_dwg_nm, map.mp_group_cd, obj.obj_x, obj.obj_y, obj.obj_typ
          		 FROM dwg_gis_mapper map, dwg_gis_object obj
         		WHERE map.obj_id = obj.obj_id
				) a
		
			order by #{dwgOrd}
		
		) aa
